function filterCards(keyword = '', category = 'æ‰€æœ‰å·¥å…·') {
    currentPage = 1;
    const lowerCaseCategory = category.toLowerCase();
    filteredRecommendations = recommendations.filter(recommendation => {
        const matchesKeyword = keyword === '' || 
            (recommendation.title + recommendation.description + recommendation.tags + recommendation.title_en + recommendation.description_en).toLowerCase().includes(keyword.toLowerCase());
        const matchesCategory = lowerCaseCategory === 'æ‰€æœ‰å·¥å…·' || 
            recommendation.tags.toLowerCase().includes(lowerCaseCategory) || 
            recommendation.title.toLowerCase().includes(lowerCaseCategory) || 
            recommendation.description.toLowerCase().includes(lowerCaseCategory);
        return matchesKeyword && matchesCategory;
    });
    renderCards();
    generatePagination();
}


