// 创建一个函数来动态地生成 HTML 内容
function generateRecommendationCard(recommendation) {
	const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);
	const isSmallScreen = window.innerWidth < 768;
	const tooltipAttr =
		isMobile && isSmallScreen
			? ""
			: `data-toggle=tooltip data-placement="bottom" title="${recommendation.tooltip}"`;

	const card = `
	        <div class="col-sm-3" data-tooltip="${recommendation.tooltip}">
	            <div class="recommendation-cards">
                    <button class="lang-switch-button"><img src="assets/images/flags/language.png" alt="切换语言" style="width: auto; height: 25px;"></button>
	                <div class="card-name" data-title="${recommendation.title}" data-title-en="${recommendation.title_en}">💡 ️${recommendation.title}</div>
	                <div class="card-description" data-description="${recommendation.description}" data-description-en="${recommendation.description_en}">${recommendation.description}</div>
	                <button class="copy-button">复制</button>
	            </div>
	        </div>
	    `;
	return card;
}

function nl2br(str) {
    return (str + '').replace(/([^>\r\n]?)(\r\n|\n\r|\r|\n)/g, '$1<br>$2');
}

let scrollPosition = 0;

function createDescriptionModal(title, description) {
    scrollPosition = window.pageYOffset; // 保存滚动位置
    $('body').addClass('no-scroll');
    $('body').css('top', -scrollPosition + 'px'); // 设置 top 值来对抗滚动位置
    
	
    return `
        <div class="description-modal">
            <div class="modal-content">
                <h2>${title}</h2>
                <p>${nl2br(description)}</p>
                <button class="close-modal">关闭</button>
            </div>
        </div>
    `;
}

$(document).on("click", ".close-modal", function () {
    $(".description-modal").remove();
    $('body').removeClass('no-scroll');
    $('body').css('top', ''); // 清除 top 值
    window.scrollTo(0, scrollPosition); // 恢复滚动位置
});



$(document).on("click", ".recommendation-cards", function (event) {
    if (event.target.classList.contains("copy-button")) {
        // 当点击复制按钮时，不显示弹出窗口
        event.stopPropagation();  // 阻止事件冒泡
        return;
    }

    const title = $(this).find(".card-name").text();
    const description = $(this).find(".card-description").text();
    const modal = createDescriptionModal(title, description);
    $("body").append(modal);
});



$(document).on("click", ".close-modal", function () {
    $(".description-modal").remove();
});


function createTooltip() {
	const tooltip = $('<div class="copy-success-tooltip" style="display: none;">已复制描述内容</div>');
	$('body').append(tooltip);
	return tooltip;
}



const itemsPerPage = 12;
