$(document).on("click", ".copy-button", function () {
	const description = $(this).siblings(".card-description").text();

	// 首先尝试使用新的 Clipboard API 来复制文本
	if (navigator.clipboard) {
		navigator.clipboard.writeText(description).then(function () {
			console.log('Copying to clipboard was successful!');
		}, function (err) {
			console.error('Could not copy text: ', err);
		});
	} else {
		// 如果新的 Clipboard API 不可用，使用旧的方法
		const $temp = $("<input>");
		$("body").append($temp);
		$temp.val(description).select();
		document.execCommand("copy");
		$temp.remove();
	}

	// 显示提示框
	const tooltip = createTooltip();
	tooltip.show().addClass("show");
	const buttonPosition = $(this).offset();
	tooltip.css({
		top: buttonPosition.top - tooltip.outerHeight() - $(this).outerHeight() / 2,
		left: buttonPosition.left + $(this).outerWidth() / 2 - tooltip.outerWidth() / 2,
	});

	// 1秒后隐藏提示框并从 DOM 中移除
	setTimeout(() => {
		tooltip.removeClass("show");
		tooltip.hide();
		tooltip.remove(); // 移除提示框元素
	}, 1000);
});










function filterCards(keyword = '', category = '所有工具') {
    currentPage = 1;
    const lowerCaseCategory = category.toLowerCase();
    filteredRecommendations = recommendations.filter(recommendation => {
        const matchesKeyword = keyword === '' || 
            (recommendation.title + recommendation.description + recommendation.tags + recommendation.title_en + recommendation.description_en).toLowerCase().includes(keyword.toLowerCase());
        const matchesCategory = lowerCaseCategory === '所有工具' || 
            recommendation.tags.toLowerCase().includes(lowerCaseCategory) || 
            recommendation.title.toLowerCase().includes(lowerCaseCategory) || 
            recommendation.description.toLowerCase().includes(lowerCaseCategory);
        return matchesKeyword && matchesCategory;
    });
    renderCards();
    generatePagination();
}


